# verify_classifier.pyfrom tensorflow.keras.models import load_modeldef verify_model(model_path):    try:        model = load_model(model_path)        print(f"Successfully loaded the model from {model_path}")        model.summary()  # Optional: Print model architecture    except Exception as e:        print(f"Failed to load the model from {model_path}")        print(f"Error: {e}")if __name__ == "__main__":    model_path = "/Users/Lerberber/Desktop/Bhat/SuperGans/CNN1_scripts/Models/CNN1D_Extended_Keras_model_final.h5"    verify_model(model_path)